import { Fragment, useEffect, useState } from "react";
import { Container, Row, Col, Alert, Button } from "reactstrap";

const ProductList = (props) => {

    const products = [
        { id: 11, name: 'soap', description: 'cleans everything', price: 100 },
        { id: 22, name: 'tools', description: 'fix things up', price: 1000 },
        { id: 333, name: 'hammer', description: 'good for the nails', price: 50 }
    ];

    useEffect( () => {
        console.log(products);
    });

    const loadProducts = () => {

    };

    return (
        <Fragment>
            <Container>
                <Row>
                    <Col lg="10" className="text-center">
                        <Alert variant="success"><b>Products in Shop</b></Alert>
                    </Col>
                    <Col lg="2">
                        <Button color="primary" onClick={loadProducts}>Load Products</Button>
                    </Col>
                </Row>
            </Container>

            {products.map((pr, i) => 
                <div key={pr.id}>
                    <div> The Index in the collection is {i}</div>
                    <Productd product={pr} />
                </div>
            )}
        </Fragment>
    );
};
export default ProductList;

const Productd = (props) => {

    const [product, setProduct] = useState(props.product);

    const divStyle = {
        border: '2px solid blue',
    };

    return (
        <div style={divStyle}>
            <h3>{product.id} - {product.name}</h3>
            <p>{product.description}</p>
            <p>Costs AUD $ {product.price}</p>
        </div>
    );
};


