import { Fragment, useEffect, useState } from "react";

const ProductList = (props) => {

    const products = [
        { id: 1, name: 'soap', description: 'cleans everything', price: 100 },
        { id: 2, name: 'tools', description: 'fix things up', price: 1000 },
        { id: 3, name: 'hammer', description: 'good for the nails', price: 50 }
    ];

    useEffect( () => {
        console.log(products);
    });


    return (
        <Fragment>

            <h1><b>This is the List of Products!!!!</b></h1>
            {products.map((pr, i) => 
                <Fragment key={i}>

                    <Productd_v1
                        name={pr.name}
                        description={pr.description}
                        price={pr.price} />

                    <Productd_v2
                        product={pr} />

                    <Productd_v3
                        product={pr} />

                </Fragment>
            )}
            
        </Fragment>
    );
};
export default ProductList;

const Productd_v1 = (props) => {

    const divStyle = {
        border: '2px solid green',
    };

    return (
        <div style={divStyle}>
            <h3>{props.name}</h3>
            <p>{props.description}</p>
            <p>Costs AUD $ {props.price}</p>
        </div>
    );
};

const Productd_v2 = (props) => {

    const divStyle = {
        border: '2px solid red',
    };

    return (
        <div style={divStyle}>
            <h3>{props.product.name}</h3>
            <p>{props.product.description}</p>
            <p>Costs AUD $ {props.product.price}</p>
        </div>
    );
};

const Productd_v3 = (props) => {

    const [product, setProduct] = useState(props.product);

    const divStyle = {
        border: '2px solid blue',
    };

    return (
        <div style={divStyle}>
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>Costs AUD $ {product.price}</p>
        </div>
    );
};
