import { Alert } from 'reactstrap';
import React, { Fragment, useState } from 'react';

const LibraryDynamic = () => {

    const [bookCollection, addBookToCollection] = useState([]);

    const books = [
        {
            name: 'Lord of the Rings',
            author: 'Tolkien',
        },
        {
            name: 'Harry Potter',
            author: 'Rowling',
        },
        {
            name: 'Falls of Kings',
            author: 'David Gemel',
        }
    ];

    return (

        <Fragment>
            <div>
                Title <input type="text" />
                Author <input type="text" />
            </div>

            <Alert variant="success">
                <p>
                    Fixed list of books in Library
                </p>
                <hr />
            </Alert>

            {books.map((book) =>
                <BookDisplay
                    key={book.id}
                    name={book.name}
                    author={book.author}
                />
            )}

        </Fragment>

    );
};
export default LibraryDynamic;

const BookDisplay = (props) => {

    return (
        <div>
            <h3>Book name: {props.name}</h3>
            <p><b>Author name: {props.author}</b></p>
        </div>
    );
}
