import { Alert, Button } from 'reactstrap';
import React, { Fragment, useState } from 'react';

const LibraryDynamic = () => {

    const [bookName, setBookName] = useState();
    const [bookAuthor, setBookAuthor] = useState();

    const handleNameChange = e => {
        setBookName(e.target.value);
    };

    const handleAuthorChange = e => {
        setBookAuthor(e.target.value);
    };

    const [bookCollection, setBookCollection] = useState([]);

    const addBookToCollection = () => {
    //    let book = { name: '1', author: 'a' };
    //    setBookCollection(book);
    };

    const books = [
        {
            name: 'Lord of the Rings',
            author: 'Tolkien',
        },
        {
            name: 'Harry Potter',
            author: 'Rowling',
        },
        {
            name: 'Falls of Kings',
            author: 'David Gemel',
        }
    ];

    return (

        <Fragment>

            <div className="container">
                <div className="Row">
                    <div className="Col">
                        Title
                    </div>
                    <div className="Col">
                        <input type="text" placeholder="Book Name" value={bookName} onChange={handleNameChange} />
                    </div>
                </div>
                <div className="Row">
                    <div className="Col">
                        Author
                    </div>
                    <div className="Col">
                        <input type="text" placeholder="Author of the Book" value={bookAuthor} onChange={handleAuthorChange} />
                        <input type="text" />
                    </div>
                </div>
                <div className="Row">
                    <div className="Col">
                        <Button onClick={addBookToCollection}>Add Book</Button>
                    </div>
                </div>
            </div>

            <Alert variant="success">
                <p>
                    Fixed list of books in Library
                </p>
                <hr />
            </Alert>

            {books.map((book) =>
                <BookDisplay
                    key={book.name}
                    name={book.name}
                    author={book.author}
                />
            )}

        </Fragment>

    );
};
export default LibraryDynamic;

const BookDisplay = (props) => {

    return (
        <div>
            <h3>Book name: {props.name}</h3>
            <p><b>Author name: {props.author}</b></p>
        </div>
    );
}
