import { Fragment, useEffect, useState } from "react";
import { Container, Row, Col, Alert, Button, Toast } from "reactstrap";
import Common from "../../controls/Common";
import ProductsDataAPI from "./Shared/ProductsDataAPI";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const ProductList = (props) => {

    const [products,setProducts] = useState([
        { productId: 11, name: 'soap', description: 'cleans everything', price: 100 },
        { productId: 22, name: 'tools', description: 'fix things up', price: 1000 },
        { productId: 333, name: 'hammer', description: 'good for the nails', price: 50 }
    ]);

    useEffect( () => {
        console.log(products);
    },[products]);

    const loadProducts = () => {
        ProductsDataAPI.getAll(null, loadProductsCallback);
    };

    const loadProductsCallback = (data) => {
        Common.log(data);
        setProducts(data);
    }

    return (
        <Container>
            <Row>
                <Col lg="10" className="text-center">
                    <Alert variant="success"><b>Products in Shop</b></Alert>
                </Col>
                <Col lg="2">
                    <Button color="primary" onClick={loadProducts}>Load Products</Button>
                </Col>
            </Row>

            <Row>
                {products.map((pr, i) => 
                    <Col lg="3" key={pr.productId}>
                        <Productd product={pr} />
                    </Col>
                )}
            </Row>


        </Container>
    );
};
export default ProductList;

const Productd = (props) => {

    const [product, setProduct] = useState(props.product);

    const divStyle = {
        border: '2px solid blue',
        margin: '2px'
    };

    return (
        <Container style={divStyle}>
            <Row>
                <Col lg="3">
                    <span>Product #<b>{product.productId}</b></span>
                    <h4>{product.name}</h4>
                </Col>
            </Row>
            <Row>
                <Col>
                    <p>{product.description}</p>
                </Col>
            </Row>
        </Container>
    );
};


