import React, { Fragment, useState, useEffect } from 'react';
import { Container, Jumbotron, Row, Col, Image, Media } from 'reactstrap';

const ProductsOLD = () => {

    const [productInStore, setProducts] = useState([{ name: 'a', description: 'b', price: 5, productId: 1 }]);

    useEffect(() => {
        console.log(productInStore);

        //setProducts([{ name: 'a', description: 'b', price: 5, productId: 1 }]);

        //const productsUri = 'https://localhost:5001/Sales';
        //fetch(productsUri)
        //    .then((res) => res.json())
        //    .then((productList) => {
        //        //console.log(productList);
        //        //setProducts(pr => [...pr, productList]);
        //        //setProducts(productList);
        //        //console.log(productInStore);
        //        //setBookCollection(book => [...book, newBook]);
        //    });
    }, []);

    return (
        <Fragment>
            <h2> Weather Report </h2>
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th>name</th>
                        <th>description</th>
                        <th>price</th>
                    </tr>
                </thead>
                <tbody>
                    {productInStore?.map((product) => {
                    <tr key={product.productId}>
                        <td>{product.name}</td>
                        <td>{product.description}</td>
                        <td>{product.price}</td>
                    </tr>
                })}
                </tbody>
            </table>

        </Fragment>
    );
};
//export default Products;


const ProductResults = [{ id: 1, name: 'book', description: 'React Fundamentals', price: 50 }, { id: 2, name: 'laptop', description: 'Dell laptop', price: 2000 }]
const Products = (props) => {

    const [productInStore, setProducts] = useState(ProductResults);

    const productsList = ProductResults.map((product) => (
        <tr>
            <td>{product.name}</td>
            <td>{product.description}</td>
            <td>{product.price}</td>
        </tr >
    ))

    return (
            <Fragment>
                <h2> Weather Report </h2>
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <th>name</th>
                            <th>description</th>
                            <th>price</th>
                        </tr>
                    </thead>
                    <tbody>
                        {productInStore?.map((product) => {
                        <tr key={product.productId}>
                            <td>{product.name}</td>
                            <td>{product.description}</td>
                            <td>{product.price}</td>
                        </tr>
                    })}
                    </tbody>
                </table>
            </Fragment>

        //<Fragment>
        //    <h2> Products </h2>
        //    <table>
        //        <tr>
        //            <th>name</th>
        //            <th>description</th>
        //            <th>price (AUD)</th>
        //        </tr>
        //        {productsList}
        //    </table>
        //</Fragment>
    );
};
export default Products;
